import React from "react";
import "../index.css";

function Tutorial() {
  return (
    <>
    <p>
      To TeX summation notation, use the \sum command. Input expressions below or above the summation using the _ and ^ characters respectively.
    </p>
    <p>
      <u>Bonus</u>: Use \displaystyle\sum to create a larger sum (this is most common).
    </p>
    </>
  );
}

export default Tutorial;
