import React from "react";
import "../index.css";

function Explanation(props) {
  return (
    <>
    <p>
      { props.lesson.explanation1 }
    </p>
    <p>
      Use \displaystyle\sum to create a larger sum (this is most common).
    </p>
    </>
  );
}

export default Explanation;
