import React, { useState } from "react";
import "../index.css";
import InputBox from "./InputBox";
import CompiledBox from "./CompiledBox";

function TypesetEx(props) {
  const [texInput, setTexInput] = useState("");

  const handleChange = event => {
    setInput(event.target.value);
  };

  return (
    <div className="row">
      <div className="container-fluid col-sm-7">
        <InputBox prompt={texInput} />
      </div>
      <div className="col-sm-5">
        <CompiledBox tex={texInput} />
      </div>
    </div>
  );
}

export default TypesetEx;
