import ReactDOM from 'react-dom';
import './index.css';
import Class from './components/ClassCatalog'

let currentClasses = [];

const firstClass = {
    type: "Math",
    image: "http://shorewoodadvancedlearning.weebly.com/uploads/8/4/8/3/84839576/press-rel-maalogo_orig.jpg",
    title: "AMC 8 Preparation",
    description: "This course acts as an introduction to the AMC (American Mathematics Competitions) AMC 8 contest. The AMC 8 provides an opportunity for middle school students to develop positive attitudes towards analytical thinking and mathematics that can assist in future careers.",
    startDate: "10/1/19",
    endDate: "11/16/19",
    days: "Mondays, Thursdays",
    time: "4:00-5:30PM",
    price: "$350"
}

currentClasses.push(firstClass);

const secondClass = {
    type: "Lego Robotics",
    image: "https://stemminds.com/wp-content/uploads/2016/12/new-ev3.jpg",
    title: "EV3: Advanced",
    description: "Take your skills to the next level in EV3: Advanced, where builds incorporate deeper concepts in physical engineering. Tackle challenges with efficient programming, relying on general solutions using infrared and color sensors.",
    startDate: "11/15/19",
    endDate: "12/20/19",
    days: "Tuesdays",
    time: "5:00-7:00PM",
    price: "$400"
}

currentClasses.push(secondClass);

const thirdClass = {
    type: "Programming",
    image: "",
    title: "Private Python Lessons",
    description: "Learn Python prog",
    startDate: "11/15/19",
    endDate: "12/20/19",
    days: "Tuesdays",
    time: "5:00-7:00PM",
    price: "$400"
}

ReactDOM.render(<div>
    { Class.apply(null, currentClasses) }
</div>, document.getElementById("root"))
