import InputBox, { getInput } from "./InputBox";
import { InlineMath } from "react-katex";
import "katex/dist/katex.min.css";

function CompiledBox() {
  const [tex, setTex] = useState("");

  const handleChange = event => {
    console.log("here");
    setTex(getInput());
  };

  return (
    <p className="tex">
      <InlineMath math={tex} />
    </p>
  );
}

function reRender() {
  CompiledBox.handl;
}

export { reRender };
export default CompiledBox;
