import React from "react";
import Header from "./Header";
import NotFoundPage from "./NotFoundPage";
import Nav from "./Nav";
import Footer from "./Footer";
import { Route, Switch } from "react-router-dom";
import WholeTutorial from "./components/WholeTutorial";

const fracs = {
  title: "Fractions",
  explanation1:
    "To write a fraction in TeX, use the \\frac command. Input the numerator and denominator in curly brackets.",
  explanation2: "",
  prompt: "-\\frac{1}{12}",
  test:
    "e = \\displaystyle\\lim_{n \\rightarrow \\infty} (1 + \\displaystyle\\frac{1}{n})^n",
  hint: "Use \\displaystyle\\frac"
};

const binoms = {
  title: "Binomial Coefficients",
  explanation1: "To write a binomial coefficient in TeX, use the \\binom command. Input the top and bottom numbers likewise to fractions, using curly brackets.",
  explanation2: "",
  prompt: "\\binom{5}{2}",
  test: "\\binom{n}{k} = \\frac{n!}{(n-k)!k!}",
  hint: "Remember, the command for fraction is \\frac"
}

const sums = {
  title: "Sums",
  explanation1:
    "To use summation notation in TeX, use the \\sum command. Input expressions below or above the summation using the _ and ^ characters respectively.",
  explanation2:
    "",
  prompt: "\\sum_{k=0}^n",
  test: "test",
  hint: "Use \\displaystyle\\sum"
};

const ints = {
  title: "Integrals",
  explanation1:
    "To write an integral in TeX, use the \\int command. Input expressions below or above the integral using the _ and ^ characters respectively.",
  explanation2:
    "Use \\displaystyle\\sum to create a larger sum (this is most commonly used).",
  prompt: "\\sum_{k=0}^n",
  test: "test",
  hint: "Use \\displaystyle\\sum"
};

function App() {
  return (
    <div className="general">
      <Header />
      <div className="row">
        <div className="col-sm-2">
          <Nav />
        </div>
        <div className="col-sm-10">
          <Switch>
            <Route
              path="/fracs"
              render={props => <WholeTutorial lesson={fracs} isAuthed={true} />}
            />
            <Route
              path="/sums"
              render={props => <WholeTutorial lesson={sums} isAuthed={true} />}
            />
            <Route component={NotFoundPage} />
          </Switch>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
