import React, { useState } from "react";
import "../index.css";
import { InlineMath } from "react-katex";
import "katex/dist/katex.min.css";
import InputBox from "./InputBox";
import CompiledBox from "./CompiledBox";

function Test(props) {
  const [text, setText] = useState("");

  const handleChange = event => {
    setText(event.target.value);
    console.log(text);
  };

  return (
    <>
      <div style={{ paddingBottom: "2%" }}>
        <p>
          TeX up the following to move on: &#160;
          <InlineMath math={props.lesson.test} />
        </p>
      </div>
      <div className="row">
        <div className="container-fluid col-sm-7">
          <InputBox text={text} handleChange={props.handleChange}/>
        </div>
        <div className="col-sm-5">
          <CompiledBox text={text}/>
        </div>
      </div>
    </>
  );
}

export default Test;
